import React, { useState } from 'react';
import axios from 'axios';
import './UpdateUserForm.css';

const UpdateUserForm = () => {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newEmail, setNewEmail] = useState('');

  const handleUpdate = (e) => {
    e.preventDefault();
    fetch('http://localhost:3003/update', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, newEmail, newPassword }),
      credentials: 'include' // Incluye cookies en la solicitud
    })
    .then(response => {
      // Verifica el tipo de contenido antes de intentar analizar la respuesta
      const contentType = response.headers.get('Content-Type');
      if (contentType && contentType.includes('application/json')) {
        return response.json();
      } else {
        return response.text(); // Devuelve el texto en caso de no ser JSON
      }
    })
    .then(data => {
      if (typeof data === 'string') {
        // Maneja el caso en que la respuesta es un texto en lugar de JSON
        alert(data);
      } else {
        alert(data.message);
      }
    })
    .catch(error => console.error('Error:', error));
  };
  

  return (
    <div className="form-wrapper">
      <h2>Update User</h2>
      <form onSubmit={handleUpdate}>
        <div className="form-control">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Email</label>
        </div>
        <div className="form-control">
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <label>New Password</label>
        </div>
        <div className="form-control">
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
          <label>New Email</label>
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateUserForm;
