import React, { useState } from 'react';
import axios from 'axios'; // Si quieres usar axios en lugar de fetch
import './UpdateUserForm.css';

const UpdateUserForm = () => {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [newEmail, setNewEmail] = useState('');

  const handleUpdate = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3003/update', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, newEmail, newPassword }),
        credentials: 'include' // Incluye cookies para autenticación
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error('Error:', error);
      alert('Failed to update user');
    }
  };

  return (
    <div className="form-wrapper">
      <h2>Update User</h2>
      <form onSubmit={handleUpdate}>
        <div className="form-control">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Email</label>
        </div>
        <div className="form-control">
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <label>New Password</label>
        </div>
        <div className="form-control">
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
          <label>New Email</label>
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateUserForm;
