// src/pages/MainPage.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
//import Header from '../components/Header/Header.js';
import './MainPage.css';

const MainPage = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    fetch('http://localhost:3002/logout', {
        method: 'POST',
        credentials: 'include' // Incluye cookies en la solicitud
      })
      .then(response => response.json())
      .then(data => {
        console.log(data.message);
        onLogout(); // Llama a la función onLogout pasada como prop
        navigate('/'); // Redirige al inicio de sesión después del logout
      })
      .catch(error => console.error('Error:', error));
  };

  return (
    <div className="main-page">
      <header className="main-header">
        <Header onLogout={handleLogout} />
      </header>
      <div className="main-content">
        <h2>Welcome to SportCom!</h2>
        {/* Aquí puedes agregar más contenido */}
      </div>
      <footer className="main-footer">
        {/* Aquí puedes agregar un pie de página si lo deseas */}
        <p>Footer Content</p>
      </footer>
    </div>
  );
};

export default MainPage;
