// src/components/LoginForm/LoginForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Importa useHistory para redirigir
import './LoginForm.css'; 

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const history = useHistory(); // Hook para redirigir

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:3001/login', {
        email,
        password
      });

      // Guarda el token JWT en una cookie
      document.cookie = `token=${response.data.token}; path=/`;

      setMessage('Login successful!');
    } catch (error) {
      console.error('Error during login:', error);
      setMessage('Error logging in');
    }
  };

  return (
    <div className="form-wrapper">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-control">
          <input
            type="email"
            required
            id="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label>Enter your email</label>
        </div>
        <div className="form-control">
          <input
            type="password"
            required
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label>Enter your password</label>
        </div>
        <button type="submit">Login</button>
        <button
          type="button"
          onClick={() => history.push('/create')} // Redirige al formulario de registro
        >
          Create Account
        </button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default LoginForm;
